mapper.insert:

	SELECT mp_board_seq.nextval FROM dual

   INSERT INTO MP_BOARD(    BNO 
   					      , TITLE 
   					      , CONTENT 
   					      , WRITER  )
                 VALUES(    #{bno}
                 		  , #{title}
                 		  , #{content}
                 		  , #{writer}  )

mapper.update:
UPDATE MP_BOARD
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content}
 WHERE BNO = #{bno}

UPDATE MP_FILE 
SET DEL_GB = 'Y'
WHERE FILE_NO = #{FILE_NO}
